import axios from 'axios';

// Function to get transactions with pagination, month, and search query
export const getTransactions = async (page, perPage, month, searchTerm) => {
  try {
    // Construct the API endpoint with query parameters
    const response = await axios.get(`/api/transactions`, {
      params: {
        page,
        perPage,
        month,
        search: searchTerm,  // Pass searchTerm as a query parameter
      },
    });
    return response;
  } catch (error) {
    console.error('Error fetching transactions:', error);
    throw error; // Re-throw error so it can be handled in the component
  }
};
