import React, { useState, useEffect } from 'react';
import { getCombinedData } from './services/api';
import TransactionTable from './components/TransactionTable';
import BarChart from './components/BarChart';
import PieChart from './components/PieChart';
import StatisticsBox from './components/StatisticsBox';

function App() {
  const [data, setData] = useState(null);
  const [month, setMonth] = useState('03'); // Default to March

  useEffect(() => {
    fetchData();
  }, [month]);

  const fetchData = async () => {
    try {
      const response = await getCombinedData(month);
      setData(response.data);
    } catch (error) {
      console.error('Error fetching combined data:', error);
    }
  };

  return (
    <div className="App">
      <h1>Transaction Dashboard</h1>
      <div>
        <label htmlFor="month-select">Select Month: </label>
        <select id="month-select" value={month} onChange={(e) => setMonth(e.target.value)}>
          <option value="01">January</option>
          <option value="02">February</option>
          <option value="03">March</option>
          <option value="04">April</option>
          <option value="05">May</option>
          <option value="06">June</option>
          <option value="07">July</option>
          <option value="08">August</option>
          <option value="09">September</option>
          <option value="10">October</option>
          <option value="11">November</option>
          <option value="12">December</option>
        </select>
      </div>

      {data ? (
        <>
          <StatisticsBox data={data.statistics} />
          <TransactionTable transactions={data.transactions.transactions} />
          <BarChart data={data.barChartData} />
          <PieChart data={data.pieChartData} />
        </>
      ) : (
        <p>Loading data...</p>
      )}
    </div>
  );
}

export default App;
