import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

export const getTransactions = (page, perPage, month, search) => {
  return axios.get(`${API_URL}/transactions`, {
    params: { page, perPage, month, search }
  });
};

export const getStatistics = (month) => {
  return axios.get(`${API_URL}/statistics`, {
    params: { month }
  });
};

export const getBarChartData = (month) => {
  return axios.get(`${API_URL}/bar-chart`, {
    params: { month }
  });
};

export const getPieChartData = (month) => {
  return axios.get(`${API_URL}/pie-chart`, {
    params: { month }
  });
};

export const getCombinedData = (month) => {
  return axios.get(`${API_URL}/combined-data`, {
    params: { month }
  });
};
