import React, { useState, useEffect } from 'react';
import TransactionTable from './components/TransactionTable';
import BarChart from './components/BarChart';
import axios from 'axios';

function App() {
  const [month, setMonth] = useState('March');
  const [transactions, setTransactions] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [page, setPage] = useState(1);
  const [stats, setStats] = useState({ totalSale: 0, totalSold: 0, totalNotSold: 0 });

  // Fetch transactions based on month, page, and searchQuery
  useEffect(() => {
    fetchTransactions();
  }, [month, page, searchQuery]);

  const fetchTransactions = async () => {
    const response = await axios.get(`/api/transactions?month=${month}&page=${page}&search=${searchQuery}`);
    setTransactions(response.data.transactions);
    setStats(response.data.stats);
  };

  return (
    <div>
      <h1>Transaction Dashboard</h1>
      <input
        type="text"
        placeholder="Search transaction"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <select value={month} onChange={(e) => setMonth(e.target.value)}>
        {['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December']
          .map(m => <option key={m} value={m}>{m}</option>)}
      </select>
      <TransactionTable transactions={transactions} page={page} setPage={setPage} />
      <div>
        <h2>Transaction Statistics</h2>
        <p>Total Sale: {stats.totalSale}</p>
        <p>Total Sold Items: {stats.totalSold}</p>
        <p>Total Not Sold Items: {stats.totalNotSold}</p>
      </div>
      <BarChart month={month} />
    </div>
  );
}

export default App;
