import React, { useState, useEffect } from 'react';
import { getTransactions } from './api'; // Update to correct import

const App = () => {
  const [transactions, setTransactions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [month, setMonth] = useState('March'); // Default to March
  const [page, setPage] = useState(1);
  const [perPage] = useState(10); // Default items per page

  // Fetch transactions based on searchTerm, month, and page
  const fetchTransactions = async () => {
    try {
      const response = await getTransactions(page, perPage, month, searchTerm);
      setTransactions(response.data.transactions); // Ensure the API returns the correct data structure
    } catch (error) {
      console.error('Error fetching transactions', error);
    }
  };

  // Trigger fetch when month, page, or searchTerm changes
  useEffect(() => {
    fetchTransactions();
  }, [month, page, searchTerm]);

  return (
    <div>
      <h1>Transaction Dashboard</h1>

      {/* Search input */}
      <input
        type="text"
        placeholder="Search transaction"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Month selection */}
      <select value={month} onChange={(e) => setMonth(e.target.value)}>
        <option value="January">January</option>
        <option value="February">February</option>
        <option value="March">March</option>
        {/* Add other months */}
      </select>

      {/* Transaction table */}
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Description</th>
            <th>Price</th>
            <th>Category</th>
            <th>Sold</th>
            <th>Image</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((transaction) => (
            <tr key={transaction.id}>
              <td>{transaction.id}</td>
              <td>{transaction.title}</td>
              <td>{transaction.description}</td>
              <td>{transaction.price}</td>
              <td>{transaction.category}</td>
              <td>{transaction.sold ? 'Yes' : 'No'}</td>
              <td>
                <img src={transaction.image} alt={transaction.title} width="50" />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination */}
      <div>
        <button onClick={() => setPage((prev) => Math.max(prev - 1, 1))}>Previous</button>
        <span> Page No: {page} </span>
        <button onClick={() => setPage((prev) => prev + 1)}>Next</button>
      </div>
    </div>
  );
};

export default App;
